import React, {useContext, useState} from 'react';
import {Context} from "../index";
import {useHistory, useLocation} from "react-router-dom";
import {LOGIN_ROUTE, REGISTRATION_ROUTE} from "../utils/consts";
import {Button, Card, Container, Form, NavLink, Row} from "react-bootstrap";

const Auth = () => {
    const {user} = useContext(Context)
    const location = useLocation()
    const history = useHistory()
    const isLogin = location.pathname === LOGIN_ROUTE
    const [email, setEmail] = useState('')
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')

    const [passwordError, setPasswordError] = useState('')
    const [usernameError, setUsernameError] = useState('')
    const [emailError, setEmailError] = useState('')



    const validation=()=>{
        if(!isLogin&&username==''){
            setUsernameError('Поле не может быть пустым');
            return false;
        }
        if(email==''){
            setEmailError('Поле не может быть пустым');
            return false;
        }
        if(password==''){
            setPasswordError('Поле не может быть пустым');
            return false;
        }
        if(!isLogin&&username.length<3){
            setPasswordError('Слишком короткое имя');
            return false;
        }
        if(password.length<3){
            setPasswordError('Слишком короткий пароль');
            return false;
        }
        if(!email.toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            )){
            setEmailError('Введите корректный e-mail');
            return false;
        }
        return true
    }
    const onChangeUsername=(text)=>{
        setUsername(text)
        setUsernameError('')
    }

    const onChangeEmail=(text)=>{
        setEmail(text)
        setEmailError('')
    }

    const onChangePassword=(text)=>{
        setPassword(text)
        setPasswordError('')
    }

    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 54}}
        >
            <Card style={{width: 600,boxShadow:"4px -4px 38px 15px rgba(34, 60, 80, 0.12)"}} className="p-5">
                <h2 className="m-auto">{isLogin ? 'Авторизация' : "Регистрация"}</h2>
                <Form className="d-flex flex-column">
                    {!isLogin?
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите ваш username"
                            value={username}
                            onChange={e => onChangeUsername(e.target.value)}
                        />
                        :
                        <></>
                    }
                    {!isLogin&&usernameError==''?
                        <></>
                        :
                        <div style={{visibility:"visible",color:"red"}}>{usernameError}</div>
                    }

                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ваш email..."
                        value={email}
                        onChange={e => onChangeEmail(e.target.value)}
                    />
                    {emailError==''?
                        <></>
                        :
                        <div style={{visibility:"visible",color:"red"}}>{emailError}</div>
                    }
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ваш пароль..."
                        value={password}
                        onChange={e => onChangePassword(e.target.value)}
                        type="password"
                    />
                    {passwordError==''?
                        <></>
                        :
                        <div style={{visibility:"visible",color:"red"}}>{passwordError}</div>
                    }
                    <Row className="d-flex justify-content-between mt-3 pl-3 pr-3">
                        {isLogin ?
                            <div>
                                Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}>Зарегистрируйся!</NavLink>
                            </div>
                            :
                            <div>
                                Есть аккаунт? <NavLink to={LOGIN_ROUTE}>Войдите!</NavLink>
                            </div>
                        }
                        <Button
                            style={{backgroundColor:"#04ddb2"}}
                        >
                            {isLogin ? 'Войти' : 'Регистрация'}
                        </Button>
                    </Row>

                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
