import React, {useContext} from 'react';
import {Container} from "react-bootstrap";
import {useHistory, useParams} from "react-router-dom";
import {Context} from "../index";

const Help = () => {
    const {help} = useContext(Context)
    const {id} = useParams();
    console.log(help.store[id]);
    const history = useHistory()

    return (
        <div className={"d-flex flex-column"}>
            <span style={{margin:"auto",textAlign:"center",marginTop:50}}><h1>Помощь</h1></span>
             <Container className={"d-flex flex-row"}>
                 <div style={{width:"20%",marginLeft:50,marginTop:50}}>
                     {help.store.map(helpItem=>
                         <div>
                             {id==helpItem.id?
                                 <div onClick={()=> history.push("/help/"+helpItem.id)} className={"p-2 mb-3"}  style={{backgroundColor:"#3be0b5",fontWeight:"bold",color:"white",borderRadius:15,cursor:"pointer"}}>
                                     <span style={{paddingLeft:15}}>{helpItem.name}</span>
                                 </div>:
                                 <div onClick={()=> history.push("/help/"+helpItem.id)} className={"p-2 mb-3"} style={{fontWeight:"bold",borderRadius:15,cursor:"pointer"}}>
                                     <span style={{paddingLeft:15}}>{helpItem.name}</span>
                                 </div>
                             }
                         </div>

                     )}
                 </div>
                 <div style={{marginTop:70,marginLeft:50}}>
                     <h2>{help.store[id].title}</h2>
                     {help.store[id].text}
                 </div>
             </Container>
        </div>
    );
};

export default Help;
