import React, {useContext, useEffect, useState} from 'react';
import {Context} from "../index";
import {useParams} from "react-router-dom";
import {Button, Container, Form, Image, Spinner} from "react-bootstrap";
import {getBlog, getOneBlog} from "../http/blogApi";

const BlogItem = () => {
    const [loading,setLoading] = useState(true)

    const {blog} = useContext(Context)
    const {id} = useParams();

    useEffect(()=>{
        getOneBlog(id).then(data=>blog.setBlog(data)).finally(()=>setLoading(false))
    },[])

    console.log(blog._store)

    if(loading){
        return <Spinner animation="grow"/>
    }
    return (
        <div className={"d-flex flex-column m-auto"} style={{width:"50%"}}>
            <h1 style={{color:'#333333',fontWeight:"bold",fontSize:60,margin:50,textAlign:"center",marginTop:100}}>
                {blog._store.attributes.title}
            </h1>
            <span className={"m-auto"} >{blog._store.attributes.publishedAt.slice(0,10)}</span>
            <div className={"mt-5 m-auto"}>
                <Image src={"http://localhost:1337"+blog._store.attributes.cover.data.attributes.url} style={{maxWidth:800, borderRadius:30}}></Image>
            </div>
            <span className={"mt-5"}>
                {blog._store.attributes.text}
            </span>

            <Form.Group className="mb-3 mt-5" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Введите ваш комментарий:</Form.Label>
                <Form.Control as="textarea" rows={8} />
                <Button variant={"success"} style={{borderRadius: 30,backgroundColor:'#04ddb2',border:"none",fontWeight:"bold",fontSize:20}} className="mt-5 p-3">Отправить сообщение</Button>
            </Form.Group>

            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <Image src={"https://www.lobe.ai/assets/avatars/avatar3@2x.jpg"} style={{maxWidth:100,maxHeight:100,borderRadius:20,marginTop:10,marginRight:20}}></Image>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    Лайк
                </div>
            </Container>

            
            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <Image src={"https://www.lobe.ai/assets/avatars/avatar3@2x.jpg"} style={{maxWidth:100,maxHeight:100,borderRadius:20,marginTop:10,marginRight:20}}></Image>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    Лайк
                </div>
            </Container>
        </div>

    );
};

export default BlogItem;
