import React, {useContext, useEffect, useState} from 'react';
import {Button, Container, Form, Image} from "react-bootstrap";
import {useParams} from "react-router-dom";
import {Context} from "../index";
import {getComments} from "../http/forumApi";

const ForumItem = () => {
    const {id} = useParams();
    const [loading,setLoading] = useState(true)
    const {forum} = useContext(Context)

    useEffect(()=>{
        getComments(id).then(data=>forum.setComment(data)).finally(()=>setLoading(false))
    },[])

    

    return (
        <div>
            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <Image src={"https://www.lobe.ai/assets/avatars/avatar1@2x.jpg"} style={{maxWidth:100,maxHeight:100,borderRadius:20,marginTop:10,marginRight:20}}></Image>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    <h2>Название темы</h2>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec facilisis id purus in mollis. Donec at varius nibh, nec efficitur risus. Quisque porta sollicitudin sapien, venenatis tristique elit fringilla et. Phasellus dapibus non libero sit amet euismod. Vivamus efficitur justo eu erat finibus, maximus pulvinar ex semper. Sed quis massa at justo porttitor pellentesque. Nullam vitae blandit lectus. Cras lacus risus, bibendum a dapibus non, lacinia nec tellus. Suspendisse potenti. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aliquam justo orci, lobortis consectetur pharetra nec, viverra id nunc. Mauris egestas gravida diam, posuere convallis eros dapibus ut. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Nulla elementum eu leo sed mattis.
                </div>
            </Container>
            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <div  className={"d-flex"} style={{backgroundColor:"#2F80ED",width:100,height:100,borderRadius:20,marginTop:10,marginRight:20}}>
                        <span style={{margin:"auto",fontWeight:"bold",color:"#fff",fontSize:30}}>A</span>
                    </div>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    Лайк
                </div>
            </Container>


            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",borderRadius:50}} className={"mt-5 d-flex flex-row"} >

            <Form.Group className="m-5" style={{width:"100%"}} controlId="exampleForm.ControlTextarea1">
                <Form.Label>Введите ваш комментарий:</Form.Label>
                <Form.Control as="textarea" rows={8} />
                <Button variant={"success"} style={{borderRadius: 30,backgroundColor:'#04ddb2',border:"none",fontWeight:"bold",fontSize:20}} className="mt-5 p-3">Отправить сообщение</Button>
            </Form.Group>
            </Container>
        </div>
    );
};

export default ForumItem;
