import React, {useContext} from 'react';
import {Context} from "../index";
import {useParams} from "react-router-dom";
import {Container, Image} from "react-bootstrap";

const BlogItem = () => {
    const {blog} = useContext(Context)
    const {id} = useParams();
    console.log(blog._store[id])
    return (
        <div className={"d-flex flex-column m-auto"} style={{width:"50%"}}>
            <h1 style={{color:'#333333',fontWeight:"bold",fontSize:60,margin:50,textAlign:"center",marginTop:100}}>
                {blog._store[id].title}
            </h1>
            <span className={"m-auto"} >15.15.15</span>
            <div className={"mt-5 m-auto"}>
                <Image src={blog._store[id].cover} style={{maxWidth:800, borderRadius:30}}></Image>
            </div>
            <span className={"mt-5"}>
                {blog._store[id].text}
            </span>

            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <Image src={"https://www.lobe.ai/assets/avatars/avatar3@2x.jpg"} style={{maxWidth:100,maxHeight:100,borderRadius:20,marginTop:10,marginRight:20}}></Image>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    Лайк
                </div>
            </Container>

            <Container style={{boxShadow:"4px 4px 38px 15px rgba(34, 60, 80, 0.12)",padding:30,borderRadius:50,paddingLeft:50,paddingRight:50}} className={"mt-5 d-flex flex-row"} >
                <div className={"d-flex flex-column"}>
                    <Image src={"https://www.lobe.ai/assets/avatars/avatar3@2x.jpg"} style={{maxWidth:100,maxHeight:100,borderRadius:20,marginTop:10,marginRight:20}}></Image>
                    <span>Author</span>
                    <p>15.15.2001</p>
                </div>
                <div  className={"mt-3"}>
                    Лайк
                </div>
            </Container>
        </div>

    );
};

export default BlogItem;
