import React, {useContext} from 'react';
import {Context} from "../index";
import {useParams} from "react-router-dom";

const BlogItem = () => {
    const {blog} = useContext(Context)
    const {id} = useParams();
    console.log(blog._store[id])
    return (
        <div>
            <h1 style={{color:'#333333',fontWeight:"bold",fontSize:60,margin:50,paddingLeft:150,paddingRight:150,textAlign:"center",marginTop:100}}>
                {blog._store[id].title}
            </h1>

        </div>
    );
};

export default BlogItem;
