import React, {useContext} from 'react';
import {Container} from "react-bootstrap";
import {useHistory, useParams} from "react-router-dom";
import {Context} from "../index";

const Help = () => {
    const {help} = useContext(Context)
    const {id} = useParams();
    console.log(id);
    console.log(help);
    console.log(id==1);
    const history = useHistory()

    return (
        <div>
         <Container>
             <div style={{width:"15%",marginLeft:50,marginTop:50}}>
                 {help.store.map(helpItem=>
                     <div>
                         {id==helpItem.id?
                             <div onClick={()=> history.push("/help/"+{id})} className={"p-2 mb-3"}  style={{backgroundColor:"#3be0b5",fontWeight:"bold",color:"white",borderRadius:15,cursor:"pointer"}}>
                                 <span style={{paddingLeft:15}}>{helpItem.name}</span>
                             </div>:
                             <div onClick={()=> history.push("/help/"+{id})} className={"p-2 mb-3"} style={{fontWeight:"bold",borderRadius:15,cursor:"pointer"}}>
                                 <span style={{paddingLeft:15}}>{helpItem.name}</span>
                             </div>
                         }
                     </div>

                 )}
             </div>
         </Container>
        </div>
    );
};

export default Help;
