import {makeAutoObservable} from "mobx";

export default class HelpStore{
    constructor() {
        this.store=[
            {id:1,key:'welcome',name:'Добро пожаловать',text:'Всем хай'},
            {id:2,key:'welcome2',name:'Добро пожаловать2',text:'Всем хай2'},
            {id:3,key:'welcome3',name:'Добро пожаловать3',text:'Всем хай3'},
        ]
        makeAutoObservable(this)
    }

    setStore(bool){
        this.store=bool
    }


}
