import React, {useContext} from 'react';
import {Button, Container, Nav, Navbar} from "react-bootstrap";
import {Context} from "../index";

const Header = () => {
    const {user} = useContext(Context)
    return (
        <Navbar bg="white" variant="light">
            <Container className="me-auto">
                <Navbar.Brand className="just">Logo</Navbar.Brand>
                <Nav.Link style={{color:"#818181"}} >Блог</Nav.Link>
                <Nav.Link style={{color:"#818181"}}  >Форум</Nav.Link>
                <Nav.Link style={{color:"#818181"}}  >Помощь</Nav.Link>

                {user.isAuth ?
                    <Button variant={"success"} className="ml-4" style={{paddingLeft:20,paddingRight:20,color:"black",borderRadius: 20,backgroundColor:'#eef0f2',border:"none"}}>Выйти</Button>
                    :
                    <Button variant={"success"}  className="ml-4" style={{paddingLeft:20,paddingRight:20,color:"black",borderRadius: 20,backgroundColor:'#eef0f2',border:"none"}}>Авторизация</Button>
                }

            </Container>
        </Navbar>
    );
};

export default Header;
